import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Square extends React.Component {

  /*
  Components can have props and state:
  1. props: key/values passed in when object is initialized
  2. state: self contained component state key/values 
  */ 

  // Constructor - set state - don't need this 
  // constructor(props){
  //   // Grab any props (so we can use if needed!)
  //   super(props);
  //   this.state = {
  //     value : null
  //   };
  // }

  render() {
    // setState() function is how you change component state - re-renders `this.setState({value: 'X'})`
    // call the parent's onClick() (passed by board) when square is clicked to update board state 
    return (
      <button 
        className="square" 
        onClick={() => this.props.onClick() }
      >
        {this.props.value}  
      </button>
    );
  }
}

class Board extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      squares : Array(9).fill(null)
    };
  }

  // Called by square - see the square props onClick() val
  handleClick(i) {
    const squares = this.state.squares.slice();
    squares[i] = 'X';
    // Store state of squares to board 
    this.setState({squares: squares});
  }

  renderSquare(i) {
    // Render square based off boards square state 
    return (
            <Square 
              value={this.state.squares[i]} 
              onClick={() => this.handleClick(i)}
            />
          );
  }

  render() {
    const status = 'Next player: X';

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="board-row">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
